#include "Piano.h"
#include "PianoKeys.h"

// play note method definition
void Piano::playNote(char note) { 

	PianoKeys Key; //piano key object

	if (note == 'a') { //check what note to be played
		Beep(261, NOTELENGTH); //play note
		Key.ColorBackGround_A(10); //draw certain key based on piano key class
		Sleep(300); 
		Key.ColorBackGround_A(16); // redraw a blank space 
		Sleep(100);
	} 
	if (note == 's') {
		Beep(293, NOTELENGTH);
		Key.ColorBackGround_S(10);
		Sleep(300);
		Key.ColorBackGround_S(16);
		Sleep(100);
	}
	if (note == 'd') {
		Beep(329, NOTELENGTH);
		Key.ColorBackGround_D(10);
		Sleep(300);
		Key.ColorBackGround_D(16);
		Sleep(100);
	}
	if (note == 'f') {
		Beep(349, NOTELENGTH);
		Key.ColorBackGround_F(10);
		Sleep(300);
		Key.ColorBackGround_F(16);
		Sleep(100);
	}
	if (note == 'g') {
		Beep(392, NOTELENGTH);
		Key.ColorBackGround_G(10);
		Sleep(300);
		Key.ColorBackGround_G(16);
		Sleep(100);
	}
	if (note == 'h') {
		Beep(440, NOTELENGTH);
		Key.ColorBackGround_H(10);
		Sleep(300);
		Key.ColorBackGround_H(16);
		Sleep(100);
	}
	if (note == 'j') {
		Beep(493, NOTELENGTH);
		Key.ColorBackGround_J(10);
		Sleep(300);
		Key.ColorBackGround_J(16);
		Sleep(100);
	}
	if (note == 'k') {
		Beep(523, NOTELENGTH);
		Key.ColorBackGround_K(10);
		Sleep(300);
		Key.ColorBackGround_K(16);
		Sleep(100);
	}
	if (note == 'l') {
		Beep(587, NOTELENGTH);
		Key.ColorBackGround_A(10);
		Sleep(300);
		Key.ColorBackGround_A(16);
		Sleep(100);
	}
	if (note == ';') {
		Beep(659, NOTELENGTH);
		Key.ColorBackGround_K(10);
		Sleep(300);
		Key.ColorBackGround_K(16);
		Sleep(100);
	}

	//rebemol mibemol solbemol labemol sibemol rebemol mibemol solbemol
	if (note == 'w') {
		Beep(277, NOTELENGTH);
		Key.ColorBackGround_W(12);
		Sleep(300);
		Key.ColorBackGround_W(15);
		Sleep(100);
	}
	if (note == 'e') {
		Beep(311, NOTELENGTH);
		Key.ColorBackGround_E(12);
		Sleep(300);
		Key.ColorBackGround_E(15);
		Sleep(100);
	}
	if (note == 't') {
		Beep(370, NOTELENGTH);
		Key.ColorBackGround_T(12);
		Sleep(300);
		Key.ColorBackGround_T(15);
		Sleep(100);
	}
	if (note == 'y') {
		Beep(415, NOTELENGTH);
		Key.ColorBackGround_Y(12);
		Sleep(300);
		Key.ColorBackGround_Y(15);
		Sleep(100);
	}
	if (note == 'u') {
		Beep(466, NOTELENGTH);
		Key.ColorBackGround_U(12);
		Sleep(300);
		Key.ColorBackGround_U(15);
		Sleep(100);
	}
	if (note == 'o') {
		Beep(554, NOTELENGTH);
		Key.ColorBackGround_W(12);
		Sleep(300);
		Key.ColorBackGround_W(15);
		Sleep(100);
	}
	if (note == 'p') {
		Beep(622, NOTELENGTH);
		Key.ColorBackGround_E(12);
		Sleep(300);
		Key.ColorBackGround_E(15);
		Sleep(100);
	}
	if (note == ']') {
		Beep(740, NOTELENGTH);
		Key.ColorBackGround_U(12);
		Sleep(300);
		Key.ColorBackGround_U(15);
		Sleep(100);
	}
	Sleep(NOTEPAUSE); // pause between notes

}

